input +.terms-showMore {
	font-family: 'Gotham Black';
	font-size: 18px;
    display: block;
    cursor: pointer;
    padding: 10px 10px;
    margin: 10px 0;
    background: #eeeeee;
    color: #777777;
	transition: background 1s;
	background-image:url(../img/add.png);
	background-repeat:no-repeat;
	background-position: 95% 50%;
}

input:checked + .terms-showMore {
	font-size: 21px;
    display: block;
    cursor: pointer;
    padding: 10px 10px;
	margin: 10px 0;
    background: #222222;
	color: #fff;
	transition: background 1s;
	background-image:url(../img/minus.png);
	background-repeat:no-repeat;
	background-position: 95% 50%;
}


input {
	display:none;
}
	input + .terms-showMore +  * {
	font-size:15px;
	padding-left:20px;
	text-align:justify;
	color:#777777;
	max-height: 0;
	/*and eventually delay an overflow:auto; */
	overflow:hidden;
	transition: max-height 1s, overflow 0s;
}
input:checked + .terms-showMore  + * { 
	/* here comes the compromise, set a max-height that would for your usual contents*/
	max-height: 2000px;
	overflow:auto;
	transition: max-height 2s, overflow 0s 0s;
}

