//------------------------------------------------------------------------------  

//------------------------------------------------------------------------------  
// CMS - Cold Brew & Liquid Concentrates
//------------------------------------------------------------------------------  

.cms-cold-brew-liquid-concentrates{

	@media (max-width:991px){
		> .cms-section {
			padding:0% 4% !important;
		}
	}
	
	.banner-section{
		background-image:url('../img/pages/cms/cblc-section-1-banner-bg-1.jpg');
		background-repeat: no-repeat;
		background-size: 100%;
		padding: 0% 10% !important;
		
		.section-content{
			padding: 2% 0;
			display: flex;
			align-items: center;
			
			.txt-content{
				width:50%;
				h1{
					color: #FFF;
					font-weight: bold;
					margin: 0 0 2% 0;
				}
				p{
					color:#FFF;
					margin: 0 0 4% 0;
				}
				.cta-btn.shop-btn{
					background: #d3901d;
					color: #FFF;
					padding: 4% 15%;
					display: inline-block;
					font-weight: bold;
					
				}
			}
			.img-content{
				width:50%;
				text-align: center;
				
				img{
					display: inline-block;
					max-width: 68%;
				}
			}
		}
		
		@media (min-width:992px){
			.section-content .txt-content{
				h1{
					font-size: 4vw;
				}
				p{
					font-size: 1.2vw;
					line-height: 1.5vw;
				}
				.cta-btn.shop-btn{
					font-size: 1.2vw;
				}
			}		
		}
		
		@media (max-width:991px){
			background-size: 100% 100%;
		}	
		
		@media (max-width:800px){
			padding: 4% !important;
			.section-content .img-content{ display:none; }
			.section-content .txt-content{ 
				width: 100%;
				text-align: center;
				.cta-btn.shop-btn{
					padding: 2% 10%;
				}
			}	
			
			
		}		
	}
	
	.products-section{
		padding: 0% 10%;
		margin-top: -5%;
		
		.section-content{
			padding: 2% 0;
			border-bottom:2px solid #d3901d;
			
			.text-banner{
				background-image:url('../img/pages/cms/cblc-section-2-banner-1.jpg');
				background-repeat: no-repeat;
				background-size: 100%;
				padding: 4%;
				color: #FFF;
				text-align: center;
				margin-bottom:1.5rem;
				
				span.extra-clr {
					color:#d3901d;
				}
				
			}
			.section-title{ 
				text-align:center; font-weight:bold; 
				margin: 0;
				padding: 0rem 0 4rem 0;
				font-size: 3rem;
				line-height: 1;

	
				
				.title-txt{
					display:inline-block;
					display: flex;
					justify-content: center;
					align-items:center;
					
					img {
						display:inline-block; 
						z-index: 1;
						@media (max-width:550px){
							display:none;
						}
					}

					
					h2{
						color: #FFF;
						margin: 0;
						background: #000;
						padding: 10px 30px;
						position:relative;
						margin-left: -20px;
						font-weight: 600;
						
						@media (max-width: 800px){
							font-size: 20px;
							height: 50px;
							line-height: 30px;
						}
						
						@media (max-width: 550px){
							margin:0;
							height:auto;
							line-height: 1;
						}
						
						&:after{
							@media (min-width:551px){
						    position: absolute;
							content: "";
							height: 100%;
							top: 0;
							width: 0;
							font-size: 0;
							line-height: 0;
							border-top: 25px solid #000;
							border-bottom: 25px solid #000;
							border-left: 20px solid #000;
							border-right: 25px solid #FFF;
							}
						}
	
					}
					
				}
				
				.save-txt{
					color:#f54e3d;
					margin-left: 6%;
					margin-top: -15px;
					
					@media (max-width:550px){
						margin: 1rem 0;
					}
				}
				.divider{
					color:#000;
					display: inline-block;
					margin: 0 5px;
				}
				
			}
			
			.item-list {
				width:100%;
				display:flex;
				flex-wrap: wrap;
				align-items: stretch;
				width: 100%;
				justify-content: space-between;
		
				.list-item{ 
					width:33%;
					margin: 1.5rem 0;
				}
				
				.product-data{
					padding: 2%;
					display: flex;
					flex-flow: column;
					justify-content: space-between;
					height: 100%;
					
					>* { text-align:center; }
					.product-info, .product-addtocart{
						display:block;
						>* { display:block; }
					}	
					.product-img img {max-width: 100px; margin-bottom:1.5rem;}
					.product-subtxt{color:#666666; font-size: 1.3rem}
					.product-name{ font-weight:500; font-size: 1.5rem;}
					.product-price{ 
						color:#d3901d;
						font-weight: bold;
						font-size: 1.6rem;
					}
					

					.product-qty-box{
							display:flex;
							flex-wrap: wrap;
							width: 100%;
							margin-bottom:1rem;
							.item-qty{
								width: 48%;
								border: 1px solid #a1916d;
								border-radius: 5px;
								margin: 1%;
								display: flex;
								flex-direction: column;
								flex-wrap: nowrap;
								justify-content: center;
								align-content: center;
								line-height: 1.5;
								padding: 1rem;
								cursor: pointer;
								
								br {display:none;}
								
								.qty-label{ font-weight:bold; font-size: 1.3rem}
								.qty-price{ 
									font-weight:bold;
									.old-price{ text-decoration: line-through; font-weight: normal; color:#666666;} 
								}
								.qty-save{ color:#d3901d; font-weight:bold;}
								
								&._active{
									background:#222222;
									* { color:#FFF !important; }
								}
							}
							
					}
					
					.btn-addtocart{
							background: #222222;
							color: #FFF;
							font-weight:bold;
							width: 100%;
							display: inline-block;
							padding: 4%;
							text-transform: uppercase;
							border:0;
					}
						
					
					.product-shipping{ font-weight:bold; color:#d3901d; padding: 4%;}
				}
				
				
			}
		
		}
		

		@media (min-width:992px){
			.section-content{
			.text-banner{
				font-size: 1.5vw;
			}
			
			.item-list {
				.product-data {
					.product-shipping{
						 font-size:1.1vw;
					}
				}
			}	
			}
		}
		
		@media (max-width:991px){
			margin-top:0;
			.section-content{
			.text-banner{
				font-size: 1.5rem;
				background-size: 100% 100%;
			}
			}
			
		}	
		
		@media (max-width:800px){
			
			.section-content{
			.item-list {
				.list-item{
					width:50%;
				}
			}
			}
		}			
		
		@media (max-width:480px){
			.section-content{
			.item-list {
				.list-item{
					width:100%;
				}
			}
			}
		}
		
		
	}
	

	.section-3{
		padding: 0% 10%;
		.section-content{
			padding: 2% 0;
			border-bottom:2px solid #d3901d;
			.section-item{
				display:flex;
				justify-content: space-between;
				align-items: center;
				padding: 5% 0;
				
				&:nth-child(even){
					.txt-content{
						order:1;
					}
					.img-content{
						order:2;
						margin-left: 5%;
