				}
				
				.txt-content{
					display: inline-block;
					width: 50%;
					text-align: left;
			
					.main-txt{
						color: #222222;
						margin: 1rem 0;
						font-weight: bold;
						font-size: 3rem;
					}
					p{
						color: #333333;
						font-size: 1.5rem;
					}
					
					.h-action-btn{
						display:flex;
						justify-content:left;
						
						.h-cta-btn {
							background: #d3901d;
							color: #FFF;
							padding: 4% 15%;
							display: inline-block;
							font-weight: bold;
							
						}
					}
				
				}
				
			}
			
		}
		

		@media (min-width:992px){
			.section-content .section-item .txt-content{
				.h-cta-btn {
					font-size: 1vw;
				}
			}	
		}
		
		@media (max-width:991px){
		
		}


		@media (max-width:800px){
			.section-content {
				.section-item{
					display: block;
					.txt-content, .img-content{
						width:100%;
						margin:0 !important;
						img{ width:100%; }
					}
					
				}
			}
		}
		
		@media (max-width:550px){
			.section-content {
				.section-item  {
					.txt-content{ 
						text-align:center; 
						.h-action-btn{
							justify-content: center;
						}
					}
				}
			}
		}
		
		
		
	}
	
	
	.section-4{
		padding: 0% 10%;
		.section-content{
			padding: 2% 0;
			
			.section-text{
				text-align:center;
				h2{ font-weight:bold; font-size: 3rem; }
				p{ max-width:60%; display:inline-block; }
			}
		
			.section-item-list{
				display:flex;
				justify-content: space-between;
				align-items: baseline;
				list-style:none;
				margin:0;
				
				.section-item{
					text-align:center;
					padding: 3% 2%;
						
					img{ display:inline-block; }
					.txt-content{
						text-align:left;
						
						.main-txt{
							color: #222222;
							margin: .75rem 0;
							font-weight: bold;
							font-size: 1.5rem;
						}
						p{
							color: #333333;
							margin:0;
						}
					}
				}
			}
			
			@media (max-width:550px){
				.section-item-list {
					display: block;
					.section-item{
						display: block;
						
						.txt-content{
							text-align:center;
						}
					}
				}
			}
		}
	}	

	.section-5{
		padding: 2% 3%;
		.section-content{
			background: #fefade;
			padding: 4% 7%;
			.section-item{
				display:flex;
				justify-content: space-between;
				align-items: center;
				
				.txt-content{
					display: inline-block;
					width: 50%;
					text-align: left;
					
