					img{ display:inline-block; width:100%;}
					.txt-content{
						text-align:center;;
						
						.main-txt{
							color: #222222;
							margin: .75rem 0;
							font-weight: bold;
							font-size: 1.6rem;
						}
						p{
							color: #333333;
							margin:0;
						}
					}
				}
			}
			
			@media (max-width:550px){
