<style>
@font-face {
    font-family: 'Gotham Black';
    src: url('../../assets/fonts/GothamBlack.woff2') format('woff2'),
        url('../../assets/GothamBlack.woff') format('woff');
    font-weight: 900;
    font-style: normal;
}
@font-face {
    font-family: 'Gotham';
    src: url('../../assets/fonts/Gotham-Book.woff2') format('woff2'),
        url('../../assets/fonts/Gotham-Book.woff') format('woff');
    font-weight: 500;
    font-style: normal;
}

@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@400;500;600;700;800;900&display=swap');

.optimizedCheckout-contentPrimary{font-family: 'Gotham';}
.optimizedCheckout-buttonPrimary{border: 1px solid #ff2a00;background-color: #ff2a00;}
.stepHeader .stepHeader-figure .stepHeader-title{color:#fff;}
.stepHeader .stepHeader-figure .stepHeader-counter{background: #222222;}
.stepHeader .stepHeader-figure{border-bottom: 2px solid #222222;background: #222222;}
.optimizedCheckout-buttonPrimary:hover, .optimizedCheckout-buttonPrimary:focus, .optimizedCheckout-buttonPrimary:active{border: 1px solid #ff2a00;color:#ff2a00;}
.optimizedCheckout-orderSummary .cart-section:last-child{background:#00b1ff}
.optimizedCheckout-orderSummary .cart-section:last-child .cart-priceItem--total .cart-priceItem-label {font-size: 18px;color: #fff;}
.optimizedCheckout-orderSummary .cart-section:last-child .cart-priceItem--total .cart-priceItem-value{font-size: 21px;color: #fff;}
.optimizedCheckout-orderSummary .cart-section:nth-child(3) .cart-priceItem .cart-priceItem-label{font-size: 18px;color:#313131}
.optimizedCheckout-orderSummary .cart-section:nth-child(3) .cart-priceItem .cart-priceItem-value{font-size: 18px;color:#222222}
.optimizedCheckout-orderSummary .cart-header .cart-title{color:#222222;font-size: 18px;font-weight:700;}
.optimizedCheckout-orderSummary .cart-header .cart-header-link{color:#222222;font-size: 18px;font-weight:700;}
.checkout-view-content .form-legend{font-size: 18px;}
.optimizedCheckout-form-label{font-size: 15px;}
.customerView{font-size: 15px;}
.optimizedCheckout-orderSummary .cart-section .product .product-title{font-size: 15px;}
