.optimizedCheckout-header{background-image:none;background:#fefadd;}
.optimizedCheckout-header .checkoutHeader-heading{text-align:center;background:#fefadd;}
.optimizedCheckout-header .checkoutHeader-link img{max-height:unset}

.optimizedCheckout-contentPrimary{font-family: 'Montserrat', sans-serif;}
.optimizedCheckout-buttonPrimary{border: 1px solid #ff2a00;background-color: #ff2a00;}
