.optimizedCheckout-orderSummary .cart-header .cart-title{color:#222222;font-size: 18px;font-weight:700;}
.optimizedCheckout-orderSummary .cart-header .cart-header-link{color:#222222;font-size: 18px;font-weight:700;}
.checkout-view-content .form-legend{font-size: 18px;}
.optimizedCheckout-form-label{font-size: 15px;}
.customerView{font-size: 15px;}
