section.cart-section.optimizedCheckout-orderSummary-cartSection a.redeemable-label {
    color: #e55942 !important;
    font-weight: 700;
    font-size: 16px;
}
section.cart-section.optimizedCheckout-orderSummary-cartSection a.redeemable-label:hover {
    color: #00b1ff!important;
}
.paymentProviderHeader-cc  .creditCardTypes-list-item:last-child,
.paymentProviderHeader-cc  .creditCardTypes-list-item:nth-last-child(4) { display:none !important; }
.optimizedCheckout-form-label[for=radio-amazon] .creditCardTypes-list-item, .optimizedCheckout-form-label[for=radio-googlepaybraintree] .creditCardTypes-list-item, .optimizedCheckout-form-label[for=radio-paypalexpress] .creditCardTypes-list-item {
    display: inline-block !important;
}
</style>
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.stor  e_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>

