            <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="{{cdn '/assets/img/home/stamped.png'}}" alt="stamped">
            {{#if show_accept_visa}}
                <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="https://cdn11.bigcommerce.com/s-3tntjfynbx/product_images/uploaded_images/payment-1.png" alt="payment-1">
