            {{/if}}
            {{#if show_accept_discover}}
                <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="https://cdn11.bigcommerce.com/s-3tntjfynbx/product_images/uploaded_images/payment-4.png" alt="payment-4">
            {{/if}}
            {{#if show_accept_sage}}
                <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="https://cdn11.bigcommerce.com/s-3tntjfynbx/product_images/uploaded_images/payment-8.png" alt="payment-8">
            {{/if}}
            {{#if show_accept_paypal}}
                <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="https://cdn11.bigcommerce.com/s-3tntjfynbx/product_images/uploaded_images/payment-5.png" alt="payment-5">
