pages/order-confirmation:
{{#partial "head"}}
{{{ checkout.checkout_head }}}
{{{ stylesheet '/assets/css/optimized-checkout.css' }}}
{{ getFontsCollection }}

<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
</script>

{{{head.scripts}}}

{{/partial}}

{{#partial "page"}}

<style>
.optimizedCheckout-header{background:unset;}
.optimizedCheckout-header .checkoutHeader-heading{text-align:center}
.optimizedCheckout-header .checkoutHeader-link img{max-height:unset}
#checkout-app{background: #fefade;}
.optimizedCheckout-orderSummary .cart-section:last-child{background:#777777;color:#fff;}
.optimizedCheckout-orderSummary .cart-header .cart-header-link{color:#222;font-size:15px;}
.optimizedCheckout-orderSummary .cart-header .cart-header-link{color:#222;font-size:15px;}
.optimizedCheckout-orderSummary .cart-section .cart-priceItem .cart-priceItem-label{color:#222;font-size:15px;}
.optimizedCheckout-orderSummary .cart-section .cart-priceItem .cart-priceItem-value{font-weight: 700;color:#222;font-size:17px;}
.optimizedCheckout-orderSummary .cart-section .product .product-title{color:#222;font-size:15px;}
.optimizedCheckout-orderSummary .cart-section .product .product-price{font-weight: 700;color:#222;font-size:17px;}
.optimizedCheckout-orderSummary .cart-section:last-child .cart-priceItem--total .cart-priceItem-label{color:#fff;font-size:15px;}
.optimizedCheckout-orderSummary .cart-section:last-child .cart-priceItem--total .cart-priceItem-value{font-weight: 700;color:#fff;font-size:17px;}
.orderConfirmation-section p:first-child{color:#00b1ff;font-size: 18px;font-weight: 700}
.orderConfirmation-section p{color:#222;font-size: 15px;}
a{color:#00b1ff;font-weight: 700;}

.optimizedCheckout-buttonSecondary{color:#fff;font-size:15px;background:#ff2a00;border:1px solid #ff2a00;padding:10px 20px;text-decoration:none;}
.optimizedCheckout-buttonSecondary:hover{background:#fff;color:#ff2a00;border:1px solid #ff2a00;}

</style>
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.store_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>

{{{ checkout.order_confirmation_content }}}

{{{ footer.scripts }}}


{{/partial}}

{{> layout/empty}}
