components/carousel:
{{#if layout '==' 'layout-1'}}
    <section class="heroCarousel"
        data-slick='{
            "dots": true,
            "arrows": false,
            "mobileFirst": true,
            "slidesToShow": 1,
            "slidesToScroll": 1,
            "adaptiveHeight": true,
            "lazyLoad": "anticipated",
            "autoplaySpeed": 7000,
            "nextArrow": "<svg class=&apos;slick-next slick-arrow&apos;><use xlink:href=&apos;#icon-slick-next&apos;></use></svg>", 
            "prevArrow": "<svg class=&apos;slick-prev slick-arrow&apos;><use xlink:href=&apos;#icon-slick-prev&apos;></use></svg>",
            "responsive": [
                {
                    "breakpoint": 1050,
                    "settings": {
                        "dots": {{dots}},
                        "arrows": {{arrows}},
                        "autoplay": true
                    }
                }
        ]}'>
        {{#each carousel.slides}}
        <a class="link-carousel" href="{{url}}">
            <div class="heroCarousel-slide {{#if ../theme_settings.homepage_stretch_carousel_images}}stretch{{/if}} {{#if @first}}heroCarousel-slide--first{{/if}} {{#if @index '==' 2}}abs_right{{/if}}">
                <div class="heroCarousel-image-wrapper" {{#and image_height image_width}}style="height: {{multiply (divide image_height image_width) 100}}vw"{{/and}}>
                    <img class="heroCarousel-image" {{#if @first}}src{{else}}src="{{cdn 'img/loading.svg'}}" data-lazy{{/if}}="{{image}}" alt="{{alt_text}}" title="{{alt_text}}"
                    {{#if image_width}}width="{{image_width}}"{{/if}} {{#if image_height}}height="{{image_height}}"{{/if}} />
                    {{#if heading}}
                        <div class="heroCarousel-title-mobile">{{{heading}}}</div>
                    {{/if}}
                </div>
                {{#if heading}}
                    {{> components/carousel-content show_background=true}}
                {{else}}
                    {{#if text}}
                        {{> components/carousel-content show_background=true}}
                    {{else}}
                        {{#if button_text}}
                            {{> components/carousel-content show_background=false}}
                        {{/if}}
                    {{/if}}
                {{/if}}
            </div>
        </a>
        {{/each}}
    </section>
{{/if}}

