components/common/quick-search-all:
{{{ snippet "forms_search"}}}
<form class="form" action="{{urls.search}}">
    <fieldset class="form-fieldset">
        <div class="form-field">
            <div id="search_category">
                <select name="category" class="select_category">
                    <option value="" selected="">All</option>
                    {{#each categories}}
                        <option value="{{id}}">{{name}}</option>
                        {{#if children}}
                            {{#each children}}
                                <option value="{{id}}">--- {{name}}</option>
                                {{#if children}}
                                    {{#each children}}
                                        <option value="{{id}}">------ {{name}}</option>
                                        {{#if children}}
                                            {{#each children}}
                                                {{#if children}}
                                                    <option value="{{id}}">--------- {{name}}</option>
                                                {{else}}
                                                    <option value="{{id}}">--------- {{name}}</option>
                                                {{/if}}
                                            {{/each}}
                                        {{/if}}
                                    {{/each}}
                                {{/if}}
                            {{/each}}
                        {{/if}}
                    {{/each}}
                </select> 
                <svg class="icon"><use xlink:href="#icon-chevron-down" /></svg>
            </div>
            <input class="form-input" data-search-quick name="search_query" id="search_query" data-error-message="{{lang 'search.error.empty_field'}}" placeholder="{{lang 'search.quick_search.input_quickSearch'}}" autocomplete="off">
            <button type="submit"><svg class="icon"><use xlink:href="#icon-search"></use></svg></button>
        </div>
    </fieldset>
</form>
<div class="quickSearchResults" data-bind="html: results"></div>

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank" rel="noopener">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}
